function checkboxHackPolyfill(e){window.onload=function(){var t=document.getElementById(e);var n=false;t.attachEvent("onclick",function(){if(n){document.body.className=document.body.className.replace(" checkbox--checked","");n=false}else{document.body.className+=" checkbox--checked";n=true}})}}